wrap_backtick <- function(x) {
  
  dplyr::if_else(x == "-" | stringr::str_detect(string = x,
                                                pattern = "^(_.*_|\\*.*\\*)$"),
                 as.character(x),
                 paste0("`", x, "`"))
}

