#' fokus::skill_question_nrs(ballot_date = "2018-09-23",
#'                           lvl = "cantonal",
#'                           canton = "aargau",
#'                           proposal_nr = 1)
#'
#' # note that by default, non-proposal-specific skill question numbers are returned
#' fokus::skill_question_nrs(ballot_date = "2018-09-23",
#'                           lvl = "cantonal",
#'                           canton = "aargau")
#'
#' fokus::skill_question_nrs(ballot_date = "2019-10-20",
