postal_dispatch_type_prose <- function(dispatch_type = all_postal_dispatch_types,
                                       lang = c("de", "en")) {
  
  dispatch_type <- rlang::arg_match(dispatch_type)
  lang <- rlang::arg_match(lang)
  
  switch(EXPR = lang,
